eth = web3.eth


def log(message):
    with open("log.txt", "a") as fp:
        new_message = f"{(request.environ['REMOTE_ADDR'] if request.environ.get('HTTP_X_FORWARDED_FOR') is None else request.environ['HTTP_X_FORWARDED_FOR'])} - [{datetime.now().strftime('%Y-%m-%d %I:%M:%S%p')}] {message} \n"
        fp.write(new_message)
        print(new_message)
    return message


@app.route("/")
def hello_world():
    log("main page")
    return render_template("main.html")


@app.route("/get_available")
def get_available():
    with open("data.json") as fp:
        cards = json.load(fp)

    return {
        card_type: [
            card_value
            for card_value in cards[card_type]
            if len(cards[card_type][card_value]) > 0
        ]
        for card_type in cards
    }


