def get_card(card_type: str, total: int, pop: bool = True):
    with open("data.json") as fp:
        cards = json.load(fp)
        if pop:
            code = cards[card_type][total].pop()
        else:
            code = cards[card_type][total][0]
    with open("data.json", "wt") as fp:
        json.dump(cards, fp, indent=2)
    log(f"Returning {code}")
    return code


