def get_card(card_type: str, total: int, pop: bool = True):
    total = str(total)
    with open("data.json") as fp:
        data = json.load(fp)
        if pop:
            code = data[card_type][total].pop()
        else:
            code = data[card_type][total][0]
    with open("data.json", "wt") as fp:
        json.dump(data, fp, indent=2)
    return code


